using Microsoft.Win32; using System; using System.Collections.Generic; using System.Linq; using System.Text; using System.Threading.Tasks; using ProjectFitness.BL.Controller; using System.Runtime.CompilerServices; using ProjectFitness.BL.Model;  namespace Fitness.CMD {     internal class Program     {         static void Main(string[] args)         {             Console.WriteLine("Вас вітає програма Fitness");              Console.WriteLine("Введіть name користувача: ");             var name = Console.ReadLine();              var userController = new UserController(name);             var eatingController = new EatingController(userController.CurrentUser);             if (userController.IsNewUser)             {                 Console.Write("Введіть стать: ");                 var gender = Console.ReadLine();                 var birthDate = ParseDateTime();                 var weight = ParseDouble("вага");                 var height = ParseDouble("зріст");                  userController.SetNewUserData(gender, birthDate, weight, height);             }              Console.WriteLine(userController.CurrentUser);              Console.WriteLine("Що ви хочете зробити?");             Console.WriteLine("Е - ввести прийом їжі");             var key = Console.ReadKey();             Console.WriteLine();              if (key.Key == ConsoleKey.E)             {                 var foods = EnterEating();                 eatingController.Add(foods.Food, foods.Weight);                  foreach(var item in eatingController.Eating.Foods)
                {
                    Console.WriteLine($"\t{item.Key} - {item.Value}");
                }             }              Console.ReadLine();         }          private static (Food Food, double Weight) EnterEating()         {             Console.Write("Введіть назву продукта: ");             var food = Console.ReadLine();              var calories = ParseDouble("калорійність");             var prots = ParseDouble("білки");             var fats = ParseDouble("жири");             var carbs = ParseDouble("вуглеводи");              var weight = ParseDouble("Вага порції");             var product = new Food(food, calories, fats, carbs, prots);              return (Food: product, Weight: weight);         }          private static DateTime ParseDateTime()         {             DateTime birthDate;             while (true)             {                 Console.Write("Введіть дату народження (dd.mm.yyyy): ");                 if (DateTime.TryParse(Console.ReadLine(), out birthDate))                 {                     break;                 }                 else                 {                     Console.WriteLine("Неправильний формат дати народження");                 }             }              return birthDate;         }          private static double ParseDouble(string name)             {                 while (true)                 {                     Console.Write($"Введіть {name}: ");                     if (double.TryParse(Console.ReadLine(), out double value))                     {                     return value;                     }                     else                     {                         Console.WriteLine($"Неправильний формат поля {name}");                     }                 }             }     } } 